package com.kam.pay.alipay.consts;/** * 微信支付异常类结果 * * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-09-28 15:09 */public class AliPayException extends Exception {    private static final long serialVersionUID = -5142931201763159420L;    /**     * 自定义错误讯息.     */    private String customErrorMsg;    public AliPayException(String customErrorMsg) {        super(customErrorMsg);        this.customErrorMsg = customErrorMsg;    }    /**     * Instantiates a new Wx pay exception.     *     * @param customErrorMsg the custom error msg     * @param tr             the tr     */    public AliPayException(String customErrorMsg, Throwable tr) {        super(customErrorMsg, tr);        this.customErrorMsg = customErrorMsg;    }}